    position: absolute;
    top: 50%;
    left: -30px;
    transform: translateY(-50%);
    transition: left 0.3s ease;
    opacity: 0;
  }

  .btn-seed-hover:hover::before {
    left: 8px;
    opacity: 1;
  }

  .btn-seed-hover:hover {
    padding-left: 2.5rem;
  }

  .scroll-indicator {
    animation: bounceGentle 2s infinite;
  }

  /* Low bandwidth mode styles */
  .low-bandwidth .animate-bounce,
  .low-bandwidth .animate-pulse,
  .low-bandwidth .animate-spin {
    animation: none !important;
  }

  .low-bandwidth .transition-all,
  .low-bandwidth .transition-transform {
    transition: none !important;
  }

  /* Focus styles for accessibility */
  .focus-visible {
    outline: 2px solid var(--ring);
    outline-offset: 2px;
  }
}

@layer utilities {
  .animate-fade-in-up {
    animation: fadeInUp 0.6s ease-out;
  }

  .animate-pulse-slow {
    animation: pulse 3s infinite;
  }

  .animate-bounce-gentle {
    animation: bounceGentle 2s infinite;
  }
}

@keyframes fadeInUp {
  from {
    opacity: 0;
    transform: translateY(30px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

@keyframes bounceGentle {
  0%, 20%, 50%, 80%, 100% {
    transform: translateY(0);
  }
  40% {
    transform: translateY(-10px);
  }
  60% {
    transform: translateY(-5px);
  }
}

/* Screen reader only content */
.sr-only {
  position: absolute;
  width: 1px;
  height: 1px;
  padding: 0;
  margin: -1px;
  overflow: hidden;
  clip: rect(0, 0, 0, 0);
  white-space: nowrap;
  border: 0;
}

